window.onload=function(){if(!document.queryCommandSupported("copy")){return;}
const higlights=document.getElementsByClassName("highlight");for(let i=0;i<higlights.length;i++){addButton(higlights[i]);}};function addButton(container){const btn=document.createElement("button");btn.className="copy-button";btn.textContent="ðŸ“‹";btn.addEventListener("click",function(){const selection=window.getSelection();selection.removeAllRanges();const range=document.createRange();range.selectNodeContents(container.firstElementChild);selection.addRange(range);document.execCommand("copy");selection.removeAllRanges();});container.appendChild(btn);}